DROP TABLE IF EXISTS `cc_assets`;
CREATE TABLE `cc_assets` (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '序号',
  `purchase_date` date DEFAULT NULL COMMENT '购买日期',
  `category` varchar(100) DEFAULT NULL COMMENT '资产类别',
  `assets_code` varchar(200) DEFAULT NULL COMMENT '固定资产编号',
  `asset_usage` varchar(100) DEFAULT NULL COMMENT '资产用途分类',
  `name` varchar(100) DEFAULT NULL COMMENT '资产名',
  `brand` varchar(50) DEFAULT NULL COMMENT '品牌',
  `quantity` varchar(20) DEFAULT NULL COMMENT '数量/单位',
  `color` varchar(30) DEFAULT NULL COMMENT '颜色',
  `memory` varchar(20) DEFAULT NULL COMMENT '内存',
  `model` varchar(200) DEFAULT NULL COMMENT '型号',
  `serial_number_meid` varchar(200) DEFAULT NULL COMMENT '序列号/MEID',
  `price` decimal(10,2) DEFAULT NULL COMMENT '购买价',
  `purchaser` varchar(50) DEFAULT NULL COMMENT '采购人',
  `consumer` varchar(50) DEFAULT NULL COMMENT '使用人',
  `consumer_department` varchar(100) DEFAULT NULL COMMENT '使用部门',
  `store` varchar(100) DEFAULT NULL COMMENT '存放地',
  `img` varchar(500) DEFAULT NULL COMMENT '资产照',
  `remarks` text COMMENT '备注',
  `create_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `update_time` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  `create_by` varchar(50) DEFAULT NULL COMMENT '创建人',
  `update_by` varchar(50) DEFAULT NULL COMMENT '更新人',
  `remark` text COMMENT '备注说明',
  `total_count` int DEFAULT '0' COMMENT '总数量',
  `remaining_count` int DEFAULT '0' COMMENT '剩余数量',
  `unit_price` decimal(10,2) DEFAULT '0.00' COMMENT '单价',
  `total_price` decimal(10,2) DEFAULT '0.00' COMMENT '总价',
  PRIMARY KEY (`id`),
  KEY `idx_asset_number` (`assets_code`),
  KEY `idx_user` (`consumer`),
  KEY `idx_department` (`consumer_department`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='固定资产盘点明细表';

